import Input from "./Input";
import SubmitButton from "./SubmitButton";
import {useState} from "react";

const Form = () => {
    const [borrowAmount, setAmount] = useState('')

    const inputBorrowAmount = (e) => {
        setAmount(e.target.value)
    }

    const submitBorrowAmount = () => {
        console.log(borrowAmount)
    }

    return (
        <>
        <div>
            <label>Amount to Borrow:</label>
            <Input value={borrowAmount} change={inputBorrowAmount} />
        </div>
        <div>
            <SubmitButton clicker={submitBorrowAmount} />
        </div>
        </>
    )
}

export default Form


