        // return inputtedValues
    }


    return (
        <>
            <div>
                <label>Amount to Borrow:</label>
                <InputAmounts value={borrowAmount} change={inputBorrowAmount} />
            </div>
             <div>
                <label>Expected Annual Salary:</label>
                <InputAmounts value={annualSalary} change={inputAnnualSalary} />
            </div>
            <div>
                <label>Percentage of monthly salary towards repayment(min:10%):</label>
                <InputAmounts value={monthlyRepay} change={inputMonthlyRepay} />
            </div>
            <div>
                <SubmitButton clicker={submitAmounts} />
            </div>
            <Calculator object={inputtedValues}/>
        </>
    )
}

export default Form
