import InputAmounts from "./InputAmounts";
import SubmitButton from "./SubmitButton";
import Calculator from "./Calculator";
import {useState} from "react";


const Form = () => {
    const [borrowAmount, setBorrowAmount] = useState('')
    const [annualSalary, setAnnualSalary] = useState('')
    const [monthlyRepay, setMonthlyRepay] = useState('')

    const inputBorrowAmount = (e) => {
        setBorrowAmount(e.target.value)
    }

    const inputAnnualSalary = (e) => {
        setAnnualSalary(e.target.value)
    }

    const inputMonthlyRepay = (e) => {
        setMonthlyRepay(e.target.value)
    }

    const submitAmounts = () => {
        let inputtedValues = {
            toBorrow: borrowAmount,
            salary: annualSalary,
            repayment: monthlyRepay
        }
      console.log(inputtedValues)
    }

    return (
        <>
            <div>
                <label>Amount to Borrow: £</label>
                <InputAmounts value={borrowAmount} change={inputBorrowAmount} />
            </div>
             <div>
                <label>Expected Annual Salary: £</label>
                <InputAmounts value={annualSalary} change={inputAnnualSalary} />
            </div>
            <div>
                <label>Percentage of monthly salary towards repayment (min:10%):
                <InputAmounts value={monthlyRepay} change={inputMonthlyRepay}/>%</label>
            </div>
            <div>
                <SubmitButton clicker={submitAmounts} />
            </div>
            <div>
                <p> You plan to borrow: £{borrowAmount}</p>
                <p> Your expected annual salary is: £  {annualSalary < '25,000' ? '25,000' : annualSalary}</p>
                <p> The percentage of your monthly salary that will go towards loan repayment is: {monthlyRepay}%</p>


            </div>
            <Calculator />
        </>
    )
}

export default Form
