import InputBorrowAmount from "./InputBorrowAmount";
import InputExpectedSalary from "./InputExpectedSalary";
import InputMonthlyRepayPercent from "./InputMonthlyRepayPercent";
import SubmitButton from "./SubmitButton";
import {useState} from "react";

const Form = () => {
    const [amounts, setAmount] = useState('')

    const inputAmounts = (e) => {
        setAmount(e.target.value)
    }

    const submitInputs = () => {
        console.log(amounts)
    }

    return (
        <>
            <div>
                <label>Amount to Borrow:</label>
                <InputBorrowAmount value={amounts} change={inputAmounts} />
            </div>
            <div>
                <label>Expected Annual Salary:</label>
                <InputExpectedSalary value={amounts} change={inputAmounts} />
            </div>
            <div>
                <label>Percentage of monthly salary towards repayment(min:10%):</label>
                <InputMonthlyRepayPercent value={amounts} change={inputAmounts} />
            </div>
            <div>
                <SubmitButton clicker={submitInputs} />
            </div>
        </>
    )
}

export default Form
