        let borrowAmount = parseInt(document.querySelector('#borrow_amount').value)
        let annualSalary = parseInt(document.querySelector('#annual_salary').value)
        let monthlyRepay = parseInt(document.querySelector('#monthly_repay').value)
        let parsedObject = {borrowAmount, annualSalary, monthlyRepay}
            console.log(parsedObject)

    return (
        <form>
            <div>
                <label>Amount to Borrow:</label>
                <input type="number" id="borrow_amount" className="form_input"/>
            </div>
            <div>
                <label>Expected Annual Salary:</label>
                <input type="number" id="annual_salary" className="form_input"/>
            </div>
            <div>
                <label>Percentage of monthly salary towards repayment(min:10%):</label>
                <input type="number" id="monthly_repay" placeholder="10" min="10" className="form_input"/>
            </div>
            <input type="submit" className="form_submit" value="Calculate monthly repayment" />
        </form>
    )
}


export default Form;
