//Form works as intended,
//  but probably needs to be broken down into multiple forms
//  to extract inputted data
//Commented code below simultaneously inputs the same value to all inputs,
//  and console.logs that value once
//So either multiple forms or separate useStates are required
//I guess the best option is an object of all the inputs in one place
// this will make it easier to transport together and do maths on
//Refer to the documentation for guidance



import InputBorrowAmount from "./InputBorrowAmount";
import SubmitButton from "./SubmitButton";
import {useState} from "react";



const Form = () => {
    const [borrowAmount, setAmount] = useState('')
    const [annualSalary, setAnnualSalary] = useState('')

    const inputBorrowAmount = (e) => {
        setAmount(e.target.value)
    }

    const inputAnnualSalary = (e) => {
        setAnnualSalary(e.target.value)
    }

    const submitAmounts = () => {
        console.log(borrowAmount)
        console.log(annualSalary)
    }

    return (
        <>
            <div>
                <label>Amount to Borrow:</label>
                <InputBorrowAmount value={borrowAmount} change={inputBorrowAmount} />
            </div>
            <div>
                <SubmitButton clicker={submitAmounts} />
            </div>
        </>
    )
}

export default Form
