import InputBorrowAmount from "./InputBorrowAmount";
import SubmitButton from "./SubmitButton";
import {useState} from "react";



const Form = () => {
    const [borrowAmount, setBorrowAmount] = useState('')
    const [annualSalary, setAnnualSalary] = useState('')
    const [monthlyRepay, setMonthlyRepay] = useState('')

    const inputBorrowAmount = (e) => {
        setBorrowAmount(e.target.value)
    }

    const inputAnnualSalary = (e) => {
        setAnnualSalary(e.target.value)
    }

    const inputMonthlyRepay = (e) => {
        setMonthlyRepay(e.target.value)
    }

    const submitAmounts = () => {
        console.log(borrowAmount)
        console.log(annualSalary)
        console.log(monthlyRepay)
    }

    const inputtedValues = {
        toBorrow: borrowAmount,
        salary: annualSalary,
        repayment: monthlyRepay
    }
    console.log(inputtedValues)

    return (
        <>
            <div>
                <label>Amount to Borrow:</label>
                <InputBorrowAmount value={borrowAmount} change={inputBorrowAmount} />
            </div>
             <div>
                <label>Expected Annual Salary:</label>
                <InputBorrowAmount value={annualSalary} change={inputAnnualSalary} />
            </div>
            <div>
                <label>Percentage of monthly salary towards repayment(min:10%):</label>
                <InputBorrowAmount value={monthlyRepay} change={inputMonthlyRepay} />
            </div>
            <div>
                <SubmitButton clicker={submitAmounts} />
            </div>
        </>
    )
}

export default Form
